        nameServers: .map(entry => ({
            name: entry.value,
        })),
        domainName: "example.com",
    });
    const domains = [
        "example.com",
        "*.example.com",
    ];
    const exampleServiceVcl = new fastly.ServiceVcl("example", {
        domains: domains.map((v, k) => ({key: k, value: v})).map(entry => ({
            name: entry.value,
