        subdomain.push(new aws.route53.Record(`subdomain-${range.key}`, {
            name: range.value,
            records: .filter(record => record.recordType == "CNAME").map(record => (record.recordValue)),
            ttl: 300,
            type: aws.route53.RecordType.CNAME,
            zoneId: production.zoneId,
        }));
    }
