package main

import (
	"fmt"
	"os"

	"github.com/pulumi/pulumi-fastly/sdk/v6/go/fastly"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
)

func readFileOrPanic(path string) pulumi.StringPtrInput {
	data, err := os.ReadFile(path)
	if err != nil {
		panic(err.Error())
	}
	return pulumi.String(string(data))
}

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := fastly.NewServiceVcl(ctx, "demo", &fastly.ServiceVclArgs{
			Backends: fastly.ServiceVclBackendArray{
				&fastly.ServiceVclBackendArgs{
					Address: pulumi.String("127.0.0.1"),
					Name:    pulumi.String("localhost"),
					Port:    pulumi.Int(80),
				},
			},
			Domains: fastly.ServiceVclDomainArray{
				&fastly.ServiceVclDomainArgs{
					Comment: pulumi.String("demo"),
					Name:    pulumi.String("demo.notexample.com"),
				},
			},
			ForceDestroy: pulumi.Bool(true),
			Vcls: fastly.ServiceVclVclArray{
				&fastly.ServiceVclVclArgs{
					Content: readFileOrPanic(fmt.Sprintf("%v/my_custom_main.vcl", path.Module)),
					Main:    pulumi.Bool(true),
					Name:    pulumi.String("my_custom_main_vcl"),
				},
				&fastly.ServiceVclVclArgs{
					Content: readFileOrPanic(fmt.Sprintf("%v/my_custom_library.vcl", path.Module)),
					Name:    pulumi.String("my_custom_library_vcl"),
				},
			},
		})
		if err != nil {
			return err
		}
		return nil
	})
}
