Basic usage:

```typescript
import * as pulumi from "@pulumi/pulumi";
import * as fastly from "@pulumi/fastly";

const demo = new fastly.ServiceVcl("demo", {
    domains: [{
        name: "example.com",
        comment: "demo",
    }],
    backends: [{
        address: "127.0.0.1",
        name: "origin1",
        port: 80,
    }],
    conditions: [
        {
            name: "WAF_Prefetch",
            type: "PREFETCH",
            statement: "req.backend.is_origin",
        },
        {
            name: "WAF_always_false",
            statement: "false",
            type: "REQUEST",
        },
    ],
    responseObjects: [{
        name: "WAF_Response",
        status: 403,
        response: "Forbidden",
        contentType: "text/html",
        content: "<html><body>Forbidden</body></html>",
        requestCondition: "WAF_always_false",
    }],
    waf: {
        prefetchCondition: "WAF_Prefetch",
        responseObject: "WAF_Response",
    },
    forceDestroy: true,
});
const waf = new fastly.ServiceWafConfiguration("waf", {
    wafId: demo.waf.apply(waf => waf?.wafId),
    httpViolationScoreThreshold: 100,
});
